                       <div><input type="radio" name="Typechoice" value="Fast food"     id="Choice2" value={type} onChange={this.changeHandler}/>Fast food</div>
                       <div><input type="radio" name="Typechoice" value="Fast casual"   id="Choice3" value={type} onChange={this.changeHandler}/>Fast casual</div>
                       <div><input type="radio" name="Typechoice" value="Casual dining" id="Choice4" value={type} onChange={this.changeHandler}/>Casual dining</div>
                       <div><input type="radio" name="Typechoice" value="Fine dining"   id="Choice5" value={type} onChange={this.changeHandler}/>Fine dining</div>
                       <div>Price level</div>
                       <div><input type="radio" name="Moneychoice" value="€"    id="Mony1" onChange={this.changeHandler}/>€</div>
