import React from "react";
import Header from "./partials/Header";
import "../App.css";
import ProductCard from "./partials/RestaurantCard";
import Search from "./Search";
//import { data } from "./Data/Data";
import FoodNotFound from "./FoodNotFound";
import axios from "axios";


function Home() {

    /*
     constructor(props) {
        super(props)

        this.state={
            message:'',
            id_owner: '',
            name:'',
            address:'',
            city:'',
            image:'',
            type:'',
            Mtype:'',
            openHr:''
        }
    }

    changeHandler = e => {
        this.setState({
            message: e.target.value[20],
            id_owner: e.target.value[20],
            name: e.target.value[20],
            address: e.target.value[20],
            city: e.target.value[20],
            image: e.target.value[20],
            type: e.target.value[20],
            Mtype: e.target.value[20],
            openHr: e.target.value[20],
        })
    }



    componentDidMount() {
      axios.get("http://localhost:5000/")
        .then(res => {
            const restaurants = res.data;

            message: this.state.message,
            id_owner: this.state.id_owner,
            name: this.state.name,
            address: this.state.address,
            city: this.state.city,
            image: this.state.image,
            type: this.state.type,
            Mtype: this.state.Mtype,
            openHr: this.state.openHr
        })
        }
            .then(res => {
                console.log(res)
            })
            .catch(err => {
                console.log(err)
            })
    }
     */




    const restaurants = () => {
        axios.get("http://localhost:5000/")
            .then(res => {
                const data = res.data;
            })
            .catch(err => console.log('error'));
    }

  // call the data from api
  const [items, setItems] = useState('');
  const [search, setSearch] = React.useState("");

  const filterList = () => {
    if (search === "") {
      return items;
    }
    return items.filter(
      (item) => item.location.toLowerCase().indexOf(search.toLowerCase()) !== -1
    );
  };

  return (
    <div className="main">
      {/* header */}
      <Header />

      <Search
        searchParam={search}
        onChange={(e) => setSearch(e.target.value)}
        placeholder="Search restaurant by city.."
      />

      {/*  */}

  

      <div class="rowbox ">
        {filterList().length > 0 ? (
          <>
            {filterList().map((item) => (
              <div class="column ">
                <ProductCard
                  restaurantsName={item.restaurantsName}
                  location={item.location}
                  address={item.address}
                  status={item.status}
                />
              </div>
            ))}
          </>
        ) : (
          <div class="column">
            <FoodNotFound />
          </div>
        )}
      </div>

    </div>
  );
}

export default Home;
